package miniwas;import java.util.HashMap;import java.util.List;import java.util.Map;public class Request {    private String methodName;    private String pathName;    private Map<String, String> headerInfo;    private Map<String, String> paramMap;    public String getMethodName() {        return methodName;    }    public String getPathName() {        return pathName;    }    public Map<String, String> getHeaderInfo() {        return headerInfo;    }    public Map<String, String> getParamMap() {        return paramMap;    }    public Request(List<String> temp) {        String[][] splits = new String[temp.size()][];        headerInfo = new HashMap<String, String>();        for (int a = 0; a < temp.size() - 1; a++) {            if (a == 0) {                splits[a] = temp.get(a).split(" ");                methodName = splits[a][0];                pathName = splits[a][1];                if (pathName.contains("?")) {                    String[] reqInfo = pathName.split("\\?");                    String[] paramInfo = reqInfo[1].split("&");                    String[] param = new String[2];                    paramMap = new HashMap<String, String>();                    for (String p : paramInfo) {                        param = p.split("=");                        paramMap.put(param[0], param[1]);                    }                    pathName = reqInfo[0];                }                continue;            }            splits[a] = temp.get(a).split(": ");            headerInfo.put(splits[a][0], splits[a][1]);        }            /*System.out.println(methodName);            System.out.println(pathName);            for (Map.Entry<String, String> entry : headerInfo.entrySet()) {                System.out.println(entry.getKey() + " : " + entry.getValue());            }            for (Map.Entry<String, String> entry : paramMap.entrySet()) {                System.out.println(entry.getKey() + " : " + entry.getValue());            }*/    }}